import logo from './logo.svg';
import './App.css';
import { useState } from 'react'

const App = () => {

  let exampleMessages = [
    {id: 1,
     text: "Message1",
     date: "1011-01-01",
     responses: [
      {id: 4,
        text: "Response1",
        date: "3033-03-03"
      },
      {id: 5,
        text: "Response2",
        date: "3033-03-03"
      },
      {id: 6,
        text: "Response3",
        date: "3033-03-03"
      },
     ]
    },
    {id: 2,
      text: "Message2",
      date: "2022-02-02",
      responses: [
        {id: 7,
          text: "Response4",
          date: "3033-03-03"
        },
        {id: 8,
          text: "Response5",
          date: "3033-03-03"
        },
        {id: 9,
          text: "Response6",
          date: "3033-03-03"
        },
       ]
    },
    {id: 3,
      text: "Message3",
      date: "3033-03-03"
    }

  ]

  const [messages, setMessages] = useState(exampleMessages)
  const [activeMessage, setActiveMessage] = useState(null)
  const [newMessage, setNewMessage] = useState('')
  const [newResponse, setNewResponse] = useState('')


  const selectMessage = (message) => {
    setActiveMessage(message)
  }

  const handleMessageChange = (event) => {
    setNewMessage(event.target.value)
  }

  const handleResponseChange = (event) => {
    setNewResponse(event.target.value)
  }

  return (
    <div>
      <h1>Message application</h1>
      <MessageList messages={messages} selectMessage={selectMessage} newMessage={newMessage} handleMessageChange={handleMessageChange}/>
      <MessageResponses activeMessage={activeMessage} newResponse={newResponse} handleResponseChange={handleResponseChange}/>
      </div>
  );
}

const MessageList = ({messages, selectMessage, handleMessageChange}) => {

  return( 
    <div>
      <ul>
        {messages.map(message => 
          <Message key={message.id} message={message} selectMessage={selectMessage}/>
        )}
      </ul>

      <form onSubmit={addNote}>
        <input
          value={newMessage}
          onChange={handleMessageChange}
        />
        <button type="submit">Post Message</button>
      </form>

    </div>
  )
}

const Message = ({message, selectMessage}) => {

  return(
    <div>
      <li onClick={() => selectMessage(message)}>{message.text}</li>
    </div>
  )

}

const MessageResponses = ({activeMessage}) => {
  if(activeMessage && activeMessage.responses){
    return(
      <div>
        <h3>{activeMessage.text}</h3>
        <ul>
        {console.log(activeMessage.responses)}
        {activeMessage.responses.map(response => 
          <Message key={response.id} message={response} selectMessage={() => {}}/>
        )}
        </ul>
      </div>
    )
  }
}

export default App;
