import logo from './logo.svg';
import './App.css';
import { useState } from 'react'

const App = () => {

  let exampleMessages = [
    {id: 1,
     text: "Message1",
     date: "1011-01-01",
     responses: [
      {id: 4,
        text: "Response1",
        date: "3033-03-03"
      },
      {id: 5,
        text: "Response2",
        date: "3033-03-03"
      },
      {id: 6,
        text: "Response3",
        date: "3033-03-03"
      },
     ]
    },
    {id: 2,
      text: "Message2",
      date: "2022-02-02"
    },
    {id: 3,
      text: "Message3",
      date: "3033-03-03"
    }

  ]

  const [messages, setMessages] = useState(exampleMessages)
  const [activeMessage, setActiveMessage] = useState(null)


  const selectMessage = (message) => {
    setActiveMessage(message)
  }

  return (
    <div>
      <h1>Message application</h1>
      <MessageList messages={messages} selectMessage={selectMessage}/>
      <MessageResponses activeMessage={activeMessage}/>
      </div>
  );
}

const MessageList = ({messages, selectMessage}) => {

  return( 
    <div>
      <ul>
        {messages.map(message => 
          <Message key={message.id} message={message} selectMessage={selectMessage}/>
        )}
      </ul>
    </div>
  )
}

const Message = ({message, selectMessage}) => {

  return(
    <div>
      <li onClick={() => selectMessage(message)}>{message.text}</li>
    </div>
  )
}

const MessageResponses = ({activeMessage}) => {
  if(activeMessage){
    return(
      <div>
        <h3>{activeMessage.text}</h3>
        {activeMessage.responses.map(response => 
          <Message key={response.id} message={response.text} selectMessage={() => {}}/>
        )}
      </div>
    )
  }
}

export default App;
